import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Image,
} from "react-native";

const Description = ({navigation, route}) => {
    useEffect(() => {
        console.log(route.params.dataDescription)
    })

  return (
    <View>
      <Image style={styles.picture} source={{uri: `${route.params.dataDescription.image}`}}/>
      <Text>{route.params.dataDescription.name}</Text>
      <Text>{route.params.dataDescription.price}</Text>
      <Text>{route.params.dataDescription.detail}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  picture: {
    height: 100,
    width: 100,
    display: block
  }
}) 

export default Description;
