import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Image,
} from "react-native";

const Home = () => {
    const [data, setData] = useState([])

    const url= "https://online.akomate.com/atma/api/products"

    useEffect(() => {
        fetch(url).then((response)=>response.json())
    }, [])

    return (
      <View
        style={{
          width: "100%",
          height: "10%",
          flexDirection: "row",
          backgroundColor: "grey",
          alignItems: "center",
          justifyContent: "flex-end"
        }}
      >
        <Text
          style={{
            fontWeight: "bold",
            fontSize: 20,
            color: "#333",
            letterSpacing: 1,
            marginLeft: 15,
            left: 0, 
            position: "absolute"
          }}
        >
          Home Page
        </Text>
        <TouchableOpacity style={{
          backgroundColor: "red",
          borderRadius: 10,
          height: 30,
          width: 70,
          alignItems: "center",
          justifyContent: "center",
          marginRight: 15,
        }}><Text>Logout</Text></TouchableOpacity>
      </View>
    );
  };

export default Home;
