import React, { useEffect, useState } from 'react';
import { View, Text, TouchableOpacity, Dimensions } from 'react-native';
import Soal1 from './src/soal1.json'

const App = () => {
  const [soal4Data, setSoal4Data] = useState([])

  const renderSoal2 = () => {
    return Soal1.map((value, index) => {
      return (
        <TouchableOpacity
          onPress={() => {
            const filterdata = Soal1.filter(item => {
              if (item.id == value.id) {
                item.done = true
              }
              return item
            })
            console.log(filterdata)
          }}
          style={{ height: 30, width: "90%" }}>
          <Text style={{ color: "#000", fontSize: 15, fontWeight: "bold", textDecorationLine: value.done ? "line-through" : "none", textDecorationColor: "red" }}>{value.nama}</Text>
        </TouchableOpacity>
      )
    })
  }

  const Soal3 = () => {
    fetch("http://jsonplaceholder.typicode.com/posts")
      .then(res => res.json())
      .then(res => {
        // Soal 3 cetak console
        console.log(res)

        // Untuk Soal 4
        setSoal4Data(res.slice(0, 10))
      })
  }

  useEffect(() => {
    Soal3()
  }, [])

  const renderSoal4 = () => {
    return soal4Data.map((value, index) => {
      return (
        <View style={{ height: 90, width: "100%", flexDirection: "row", borderWidth: 1 }}>
          <View style={{ height: 90, width: 20, borderWidth: 1 }}>
            <Text>{value.id}</Text>
          </View>
          <View style={{ height: 90, width: Dimensions.get("window").width / 3, borderWidth: 1 }}>
            <Text>{value.title}</Text>
          </View>
          <View style={{ height: 90, width: "30%", borderWidth: 1 }}>
            <Text>{value.body}</Text>
          </View>
          <View style={{ height: 90, width: "30%", borderWidth: 1, flexDirection:"row", alignItems:"center", justifyContent:"space-around" }}>
            {/* Soal 5 */}
            <TouchableOpacity 
            onPress={() => {
              const soal5 = Soal1.filter(item => item.id != value.id)
              setSoal4Data(soal5)
            }}
            style={{height:50, width:120, backgroundColor:"red", borderRadius:20, alignItems:"center", justifyContent:"center"}}>
              <Text style={{color:"#fff", fontWeight:"bold", fontSize:10, textAlign:"center"}} >SOAL 5 : Hapus Data</Text>
            </TouchableOpacity>
            {/* Soal 6 */}
            <TouchableOpacity style={{height:50, width:120, backgroundColor:"red", borderRadius:20, alignItems:"center", justifyContent:"center"}}>
              <Text style={{color:"#fff", fontWeight:"bold", fontSize:10, textAlign:"center"}} >SOAL 6 : Hapus Key Object</Text>
            </TouchableOpacity>
          </View>
        </View>
      )
    })
  }

  return (
    <View style={{ flex: 1 }}>
      <Text style={{ fontSize: 20, fontWeight: "bold" }}>SOAL 2: Menampilkan data json SOAL 1</Text>
      {renderSoal2()}
      <Text style={{ fontSize: 20, fontWeight: "bold", marginTop: 20, marginBottom: 5 }}>SOAL 4: Menampilkan data json SOAL 3</Text>
      {renderSoal4()}
    </View>
  )
}

export default App
