import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Image
} from "react-native";

const Register = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <View style={styles.box}>
      <Text style={styles.text}>Register Page</Text>
      <Image style={styles.image} source={{uri: "https://i.ibb.co/Pm53kf8/584830f5cef1014c0b5e4aa1-1.png"}}/>
      <View style={styles.inputField}>
        <Text>Name: </Text>
        <TextInput style={styles.input} placeholder="please input your name"/>
      </View>
      <View style={styles.inputField}>
        <Text>Email: </Text>
        <TextInput style={styles.input} placeholder="please input your email"/>
      </View>
      <View style={styles.inputField}>
        <Text>Username: </Text>
        <TextInput style={styles.input} placeholder="please input your username"/>
      </View>
      <View style={styles.inputField}>
        <Text>Password: </Text>
        <TextInput style={styles.input} placeholder="please input your password"/>
      </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  box: {
    borderWidth: 2,
    borderRadius: 15,
    height: 500,
    width: 500,
    margin: 5,
  },
  text: {
    justifyContent: "center",
    fontSize: 30,
    paddingLeft: 15
  },
  image: {
    marginTop: 15,
  },
  inputField: {
    marginLeft: 15,
    flexDirection: "row"
  },
  input: {
    borderWidth: 1,
    borderRadius: 5,
    height: 25,
    width: 300,
  }
});

export default Register;
