import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Image,
} from "react-native";

const Login = ({ navigation }) => {
    return (
        <View style={styles.container}>
          <View style={styles.box}>
            <View style={styles.container}>
              <Text style={styles.text}>Login Page</Text>
              <Image
                style={styles.image}
                source={{
                  uri: "https://i.ibb.co/Pm53kf8/584830f5cef1014c0b5e4aa1-1.png",
                }}
              />
            </View>
            <View style={styles.inputField}>
              <Text style={styles.inputText}>Username: </Text>
              <TextInput
                style={styles.input}
                placeholder="please input your username"
              />
            </View>
            <View style={styles.inputField}>
              <Text style={styles.inputText}>Password: </Text>
              <TextInput
                style={styles.input}
                placeholder="please input your password"
              />
            </View>
            <TouchableOpacity style={{ marginTop: 20, alignItems: "center" }} onPress={() => {
                navigation.navigate("Register")
            }}>
              <Text>Don't have an account? click here to create an acount!</Text>
            </TouchableOpacity>
            <View style={styles.container}>
              <TouchableOpacity
                style={{
                  marginTop: 30,
                  justifyContent: "center",
                  alignItems: "center",
                  height: 30,
                  backgroundColor: "blue",
                  width: 100,
                  borderRadius: 5,
                }}
                on
              >
                <Text style={{ color: "white" }}>Login</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      );
    };
    
    const styles = StyleSheet.create({
      container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "center",
      },
      box: {
        borderWidth: 2,
        borderRadius: 15,
        height: 500,
        width: 450,
        margin: 5,
      },
      text: {
        justifyContent: "center",
        fontSize: 30,
        paddingLeft: 15,
      },
      image: {
        marginTop: 15,
        height: 15,
      },
      inputField: {
        marginLeft: 15,
        flexDirection: "row",
        marginTop: 15,
      },
      input: {
        borderWidth: 1,
        borderRadius: 5,
        height: 25,
        width: 300,
      },
      inputText: {
        width: 100,
      },
    });
export default Login;
