      setListDetaiProduct(value)
    }
    }

    return route.params.items.map((value, index) => {
        return(
            <View>
                <Text>{value.name}</Text>
                <Text>{value.price}</Text>
                <Text>{value.detail}</Text>
                <Text>{value.id}</Text>
                </View>
        )
    })
    
}

export default Cart
