import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Image,
} from "react-native";

const Home = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  const url = "https://online.akomate.com/atma/api/products";

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, []);

  return (
    <>
      <View
        style={{
          width: "100%",
          height: "10%",
          flexDirection: "row",
          backgroundColor: "grey",
          alignItems: "center",
          justifyContent: "flex-end",
        }}
      >
        <Text
          style={{
            fontWeight: "bold",
            fontSize: 20,
            color: "#333",
            letterSpacing: 1,
            marginLeft: 15,
            left: 0,
            position: "absolute",
          }}
        >
          Home Page
        </Text>
        <TouchableOpacity
          style={{
            backgroundColor: "red",
            borderRadius: 10,
            height: 30,
            width: 70,
            alignItems: "center",
            justifyContent: "center",
            marginRight: 15,
          }}
        >
          <Text>Logout</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.container}>
        {loading ? (
          <Text>Loading ....</Text>
        ) : (
          data.map((items) => {
            return (
              <View
                style={{
                  height: 300,
                  width: "45%",
                  margin: 10,
                  shadowColor: "#000",
shadowOffset: {
	width: 0,
	height: 2,
},
shadowOpacity: 0.25,
shadowRadius: 3.84,

elevation: 5,
                }}
              >
                <TouchableOpacity>
                  <Image source={{uri: items.image}} />
                </TouchableOpacity>
                <Text>{items.name}</Text>
                <Text>{items.detail}</Text>
                <Text>{items.price}</Text>
              </View>
            );
          })
        )}
      </View>
    </>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "center"
  },
});

export default Home;
