import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Image,
} from "react-native";

const Home = ({ navigation }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [cart, setCart] = useState([])

  const url = "https://online.akomate.com/atma/api/products";

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, []);

  return (
    <>
      <View
        style={{
          width: "100%",
          height: "10%",
          flexDirection: "row",
          backgroundColor: "grey",
          alignItems: "center",
          justifyContent: "flex-end",
        }}
      >
        <View style={{left: 0,
            position: "absolute",
            flexDirection: "row"}}>
        <Text
          style={{
            fontWeight: "bold",
            fontSize: 20,
            color: "#333",
            letterSpacing: 1,
            marginLeft: 15,
          }}
        >
          Home Page
        </Text>
        <TouchableOpacity style={{marginRight: 20}}><Text style={{fontSize: 20,}}>Cart {cart.length}</Text></TouchableOpacity>
        </View>
        <TextInput
          style={{
            borderWidth: 1,
            width: 400,
            height: 30,
            marginRight: 10,
            borderRadius: 5,
            textAlign: "right",
            paddingRight: 10,
          }}
          onChangeText={(input) => {
            setSearch(input);
          }}
          placeholder="type to search!"
        />
        <TouchableOpacity
          style={{
            backgroundColor: "red",
            borderRadius: 10,
            height: 30,
            width: 70,
            alignItems: "center",
            justifyContent: "center",
            marginRight: 15,
          }} onPress={() => {
            navigation.navigate("Login")
          }}
        >
          <Text>Logout</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.container}>
        {loading ? (
          <Text>Loading ....</Text>
        ) : (
          data
            .filter((element) => {
              return element.name.includes(search);
            })
            .map((items) => {
              return (
                <View
                  style={{
                    borderRadius: 15,
                    height: 400,
                    width: "45%",
                    margin: 10,
                    shadowColor: "#000",
                    shadowOffset: {
                      width: 0,
                      height: 2,
                    },
                    shadowOpacity: 0.25,
                    shadowRadius: 3.84,

                    elevation: 5,
                  }}
                >
                  <TouchableOpacity
                    style={{ width: "100%" }}
                    onPress={() => {
                      navigation.navigate("Description", {
                        dataDescription: items,
                      });
                    }}
                  >
                    <Image
                      style={{
                        height: 175,
                        width: "100%",
                        resizeMode: "stretch",
                        borderRadius: 15,
                      }}
                      source={{ uri: `${items.image}` }}
                    />
                  </TouchableOpacity>
                  <Text
                    style={{ fontSize: 19, fontWeight: "bold", marginLeft: 10 }}
                  >
                    {items.name}
                  </Text>
                  <Text
                    style={{
                      fontSize: 15,
                      marginTop: 10,
                      height: 100,
                      width: "100%",
                      paddingLeft: 10,
                      paddingRight: 10,
                    }}
                  >
                    {items.detail.length <= 50 ? (
                      items.detail
                    ) : (
                      <Text>{items.detail.slice(0, 50)}...</Text>
                    )}
                  </Text>
                  <View
                    style={{
                      flexDirection: "row",
                      height: 60,
                      width: "100%",
                      justifyContent: "space-between",
                      alignItems: "flex-end",
                    }}
                  >
                    <Text style={{ marginLeft: 10, fontSize: 15 }}>
                      RP. {items.price}
                    </Text>
                    <TouchableOpacity
                      style={{
                        marginRight: 10,
                        backgroundColor: "blue",
                        height: 35,
                        width: 85,
                        borderRadius: 10,
                        justifyContent: "center",
                        alignItems: "center",
                      }} onPress={{}}
                    >
                      <Text style={{ color: "white" }}>Add to Cart</Text>
                    </TouchableOpacity>
                  </View>
                </View>
              );
            })
        )}
      </View>
    </>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "center",
  },
});

export default Home;
