import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Image,
} from "react-native";

const Register = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <View style={styles.box}>
        <View>
          <Text style={styles.text}>Register Page</Text>
          <Image
            style={styles.image}
            source={{
              uri: "https://i.ibb.co/Pm53kf8/584830f5cef1014c0b5e4aa1-1.png",
            }}
          />
        </View>
        <View style={styles.inputField}>
          <Text style={styles.inputText}>Name: </Text>
          <TextInput
            style={styles.input}
            placeholder="please input your name"
          />
        </View>
        <View style={styles.inputField}>
          <Text style={styles.inputText}>Email: </Text>
          <TextInput
            style={styles.input}
            placeholder="please input your email"
          />
        </View>
        <View style={styles.inputField}>
          <Text style={styles.inputText}>Username: </Text>
          <TextInput
            style={styles.input}
            placeholder="please input your username"
          />
        </View>
        <View style={styles.inputField}>
          <Text style={styles.inputText}>Password: </Text>
          <TextInput
            style={styles.input}
            placeholder="please input your password"
          />
        </View>
        <TouchableOpacity style={{marginTop: 10, alignItems: "center",}}><Text>Already have an account? click here to login!</Text></TouchableOpacity>
        <TouchableOpacity style={{marginTop: 10, alignItems: "center",}}><Text>Register</Text></TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  box: {
    borderWidth: 2,
    borderRadius: 15,
    height: 500,
    width: 450,
    margin: 5,
  },
  text: {
    justifyContent: "center",
    fontSize: 30,
    paddingLeft: 15,
  },
  image: {
    marginTop: 15,
    height: 100,
  },
  inputField: {
    marginLeft: 15,
    flexDirection: "row",
    marginTop: 15,
  },
  input: {
    borderWidth: 1,
    borderRadius: 5,
    height: 25,
    width: 300,
  },
  inputText: {
    width: 100,
  },
});

export default Register;
