import React, { useEffect, useState } from 'react';
import { View, Text, TouchableOpacity } from 'react-native';
import Soal1 from './src/soal1.json'

const App = () => {

  const renderSoal2 = () => {
    return Soal1.map((value, index) => {
      return (
        <TouchableOpacity style={{ height: 30, width: "90%" }}>
          <Text style={{ color: "#000", fontSize: 15, fontWeight: "bold", textDecorationLine: value.done ? "underline" : "none", textDecorationColor:"red" }}>{value.nama}</Text>
        </TouchableOpacity>
      )
    })
  }
  return (
    <View style={{ flex: 1 }}>

    </View>
  )
}

export default App
